mapper.select:
select

  distinct


from attendance




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}



  for update

SELECT

FROM
attendance a
LEFT JOIN staff_info s ON a.staff_no = s.staff_no

     and order_no = #{orderNo}
     and staff_no = #{staffNo}
     and statistics_year = #{statisticsYear}
     and department_id = #{departmentId}
     and statistics_month = #{statisticsMonth}
     and serve_days_expect = #{serveDaysExpect}
     and serve_days_actual = #{serveDaysActual}
     and status = #{status}
     and staff_name = #{staffName}

        AND date_format(a.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


        AND date_format(a.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

SELECT
FROM attendance a LEFT JOIN staff_info s ON a.staff_no = s.staff_no WHERE a.auto_id in
#{id}

SELECT

FROM
attendance a
LEFT JOIN staff_info s ON a.staff_no = s.staff_no

     and department_id = #{departmentId}
     and order_no like concat('%', #{orderNo}, '%')
     and staff_no = #{staffNo}
     and statistics_year = #{statisticsYear}
     and statistics_month = #{statisticsMonth}
     and serve_days_expect = #{serveDaysExpect}
     and serve_days_actual = #{serveDaysActual}
     and status = #{status}
     and staff_name like concat('%', #{staffName}, '%')

        AND date_format(a.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


        AND date_format(a.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

SELECT
FROM attendance a LEFT JOIN staff_info s ON a.staff_no = s.staff_no where a.auto_id in
#{id}

mapper.update:
update attendance
order_no = #{orderNo}, staff_no = #{staffNo}, statistics_year = #{statisticsYear}, statistics_month = #{statisticsMonth}, serve_days_expect = #{serveDaysExpect}, serve_days_actual = #{serveDaysActual}, create_time = #{createTime}, status = #{status}, staff_name = #{staffName},
where auto_id = #{autoId}

update attendance
set serve_days_expect = #{serveDaysExpect},serve_days_expect = #{serveDaysExpect}
where auto_id = #{autoId}
