mapper.select:
select a.statistics_year ,a.statistics_month,a.staff_no, dep.department_name,pro.project_name,sta.order_no,pos.post_name,sta.staff_name,sup.supplier_name,ll.bid_price,a.serve_days_expect,a.serve_days_actual,sta.arrive_date,sta.leave_date,a.remark from attendance a left join staff_info sta on sta.staff_no=a.staff_no left join project_info pro on pro.project_id=sta.project_id left join department_info dep on dep.department_id = sta.department_id left join supplier_info sup on sup.supplier_id = sta.supplier_id left join post_info pos on pos.post_id=sta.post_id LEFT JOIN (select tmp.supplier_id, tmp.post_id,tmp.bid_price,ag.agreement_no,ag.agreement_start,ag.agreement_end from supplier_bid tmp left join agreement_info ag on tmp.agreement_id=ag.agreement_id) as ll on ll.supplier_id=sta.supplier_id and ll.post_id = sta.post_id
ll.agreement_start <= date_format(now(),'%y%m%d') and ll.agreement_end >= date_format(now(),'%y%m%d')
and a.order_no like concat('%', #{orderNo}, '%')
and a.staff_no = #{staffNo,jdbcType=INTEGER}
and a.statistics_year = #{statisticsYear,jdbcType=SMALLINT}
and a.statistics_month = #{statisticsMonth,jdbcType=TINYINT}
and a.serve_days_expect = #{serveDaysExpect,jdbcType=REAL}
and a.serve_days_actual = #{serveDaysActual,jdbcType=REAL}
group by dep.department_id,pro.project_id,sta.order_no

select a.statistics_year ,a.statistics_month,a.staff_no, dep.department_name,pro.project_name,inter.order_no,pos.post_name,sta.staff_name,sup.supplier_name,ll.bid_price,a.serve_days_expect,a.serve_days_actual,sta.arrive_date,sta.leave_date,a.remark from attendance a left join staff_info sta on sta.staff_no=a.staff_no left join interview_info inter on inter.order_no = sta.order_no left join review_info re on re.purchase_no = inter.purchase_no and re.post_id = sta.post_id left join project_info pro on pro.project_id=re.project_id left join department_info dep on dep.department_id = sta.department_id left join supplier_info sup on sup.supplier_id = sta.supplier_id left join post_info pos on pos.post_id=sta.post_id LEFT JOIN (select tmp.supplier_id, tmp.post_id,tmp.bid_price,ag.agreement_no,ag.agreement_start,ag.agreement_end from supplier_bid tmp left join agreement_info ag on tmp.agreement_id=ag.agreement_id) as ll on ll.supplier_id=sta.supplier_id and ll.post_id = sta.post_id
ll.agreement_start <= date_format(now(),'%y%m%d') and ll.agreement_end >= date_format(now(),'%y%m%d')
and a.statistics_year = #{statisticsYear,jdbcType=SMALLINT}
and a.statistics_month = #{statisticsMonth,jdbcType=TINYINT}
and a.staff_no in
#{ids}

select auto_id,statistics_year,statistics_month,department_name,project_name,order_no,post_name,staff_name,staff_no,supplier_name,bid_price,serve_days_expect,serve_days_actual,public_vacation,arrive_date,leave_date,remark,post_expenses,create_time
from month_bill
where statistics_year=#{statisticsYear} and statistics_month=#{statisticsMonth}

mapper.update:
update month_bill
set post_expenses = #{postExpenses}
where auto_id = #{autoId}
