mapper.select:
select oper_id, title, business_type, method, request_method, operator_type, oper_name, oper_url, oper_param, status, error_msg, oper_time
from sys_oper_log


    AND title like concat('%', #{title}, '%')


    AND business_type = #{businessType}


    AND business_type in

      #{businessType}



    AND status = #{status}


    AND oper_name like concat('%', #{operName}, '%')


    and date_format(oper_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


    and date_format(oper_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
